import { fetchGetMjPromptAssociateApi, fetchGetMjPromptFanyiApi } from '@/api/index'
import Loading from '@/components/base/Loading.vue'
import { useAppStore, useAuthStore } from '@/store'
import { copyText } from '@/utils/format'
import failImg from '@/assets/fail.png'
import drawSvg from '@/assets/icons/draw.svg'
import zoomSvg from '@/assets/icons/zoom.svg'
import marketImg from '@/assets/market.png'

interface PromptItem {
	status: boolean
	title: string
	isCarryParams: boolean
}

const containerRef = ref<HTMLElement | null>(null)
const router = useRouter()
const authStore = useAuthStore()
const isLogin = computed(() => authStore.isLogin)
const userBalance = computed(() => authStore.userBalance)
const sumDrawMjCount = computed(() => {
  return userBalance.value.sumDrawMjCount || 0
})
const appStore = useAppStore()

const theme = computed(() => appStore.theme)
const loadingTextColor = computed(() => theme.value === 'dark' ? '#fff' : '#000')
const ms = useMessage()
const dialog = useDialog()
const { isMobile } = useBasicLayout()
const uploadUrl = ref(`${import.meta.env.VITE_GLOB_API_URL}/upload/file`)
const downloadUrl = `${import.meta.env.VITE_GLOB_API_URL}/midjourney/download`
let isLoopIn = false
let timer: any = null
const aspect = ref('9:16')
const model = ref('MJ')
const version = ref('5.2')
const style = ref(0)
const quality = ref('1')
const stylize = ref(100)
const chaos = ref(0)
const prompt = ref('')
const noPrompt = ref('')
const drawList: any = ref([])
const countQueue = ref(0)
const refreshLoading = ref(false)
const associateLoading = ref(false)
const translateLoading = ref(false)
const promptList = ref<PromptItem[]>([])
const size = ref(12)
const totalCount = ref(0)
const carryOptions = ref(1)
const submitDisabled = computed(() => {
  return !prompt.value || associateLoading.value || translateLoading.value || translateNoLoading.value
})

const dataBase64 = ref('')
let curFile: File | null

watch(isLogin, async (newVal, oldVal) => {
  if (newVal && !oldVal)
    await queryDrawResult()
})

const isMore = computed(() => totalCount.value > size.value)

const sizeList = [
  { aspect: '1:1', width: '100%', height: '100%' },
  { aspect: '4:3', width: '100%', height: '75%' },
  { aspect: '3:4', width: '75%', height: '100%' },
  { aspect: '16:9', width: '100%', height: '57%' },
  { aspect: '9:16', width: '57%', height: '100%' },
]

const styleOptions = [
  { label: '默认风格', value: 0 },
  { label: '表现力风格', value: 'expressive' },
  { label: '可爱风格', value: 'cute' },
  { label: '景观风格', value: 'scenic' },
]

const qualityOptions = [
  { label: '普通', value: '.25' },
  { label: '一般', value: '.5' },
  { label: '高清', value: '1' },
  { label: '超高清', value: '2' },
]

const versionOptions = computed(() => {
  if (model.value === 'MJ') {
    return [
      { label: '5.2', value: '5.2' },
      { label: '5.1', value: '5.1' },
      { label: '5', value: '5' },
      { label: '4', value: '4' }]
  }
  if (model.value === 'NIJI') {
    return [
      { label: '5', value: '5' },
      { label: '4', value: '4' },
    ]
  }
  return []
})

const modelList = [
  { name: 'MJ', img: mjImg, val: 'mj' },
  { name: 'NIJI', img: nijiImg, val: 'niji' },
]

const activeAspect = computed(() => (item: string) => {
  return aspect.value === item
})

const activeModel = computed(() => (item: string) => {
  return model.value === item
})

function handleFileSelect(event: any) {
  const file = event?.target?.files[0]
  handleSetFile(file)
}

function handleDrop(event: any) {
  event.preventDefault()
  const file = event.dataTransfer.files[0]
  handleSetFile(file)
}

async function handleSetFile(file: File) {
  curFile = file
  const reader = new FileReader()
  reader.onload = (event: any) => {
    dataBase64.value = event.target?.result as string
  }
  reader.readAsDataURL(file)
}


async function hanleQueryPrompts(){
	const res: any = await fetchMidjourneyPromptList()
	promptList.value = res.data.filter( item => item.status)
}

async function queryAllDrawList() {
  const res: ResData = await fetchMidjourneyDrawList({ page: 1, size: size.value })
  const { rows, countQueue: queueCount, count } = res.data
  drawList.value = rows || []
  totalCount.value = count
  countQueue.value = queueCount || 0
}

/* 翻译prompt */
async function handleFanyiPrompt() {
  if (!prompt.value)
    return ms.warning('请输入描述词！')
  translateLoading.value = true
  try {
    const res: ResData = await fetchGetMjPromptFanyiApi({ prompt: prompt.value })
    if (!res.success)
      return ms.error('翻译失败了！')
    prompt.value = res.data
    translateLoading.value = false
  }
  catch (error) {
    translateLoading.value = false
  }
}
const translateNoLoading = ref(false)
/* 翻译不需要的元素 */
async function handleFanyiNoPrompt() {
  if (!noPrompt.value)
    return ms.warning('请输入描述词！')
		translateNoLoading.value = true
  try {
    const res: ResData = await fetchGetMjPromptFanyiApi({ prompt: noPrompt.value })
    if (!res.success)
      return ms.error('翻译失败了！')
    noPrompt.value = res.data
    translateNoLoading.value = false
  }
  catch (error) {
    translateNoLoading.value = false
  }
}

/* 联想描述词 */
async function handleAssociatePrompt() {
  if (!prompt.value)
    return ms.warning('请输入描述词！')
  associateLoading.value = true
  try {
    const res: ResData = await fetchGetMjPromptAssociateApi({ prompt: prompt.value })
    if (!res.success)
      return ms.error('联想失败了')
    prompt.value = res.data
    associateLoading.value = false
  }
  catch (error) {
    associateLoading.value = false
  }
}

/* 翻译不需要元素描述词 */
// async function handleTranslateNoPrompt() {
//   if (!hasChinese(noPrompt.value))
//     return ms.warning('当前描述词无需翻译！')
//   noPrompt.value = await translate(noPrompt.value)
// }

/* 检测是否需要翻译 */
function hasChinese(str: string) {
  const reg = /[\u4E00-\u9FA5]/g
  return reg.test(str)
}

/* 移除prompt中的指令 */
// function removeDirectives(str: string) {
//   const reg = /--\S+/g
//   return str.replace(reg, '')
// }

/* 移除用户自己的指令 */
function removeParameters(str: string) {
  const regex = /--\w+\s\S+/g
  return str.replace(regex, '')
}

/* 格式化绘画参数 */
function formatParams() {
  if (!carryOptions.value)
    return ''
  let formatPropmpt = ''
  /* 不需要的内容 */
  noPrompt.value && (formatPropmpt += ` --no ${noPrompt.value}`)
  /* 模型+版本 */
  if (model.value === 'MJ' && version.value) {
    formatPropmpt += ` --v ${version.value}`
    formatPropmpt += ` --s ${stylize.value}`
  }
  if (model.value === 'NIJI' && version.value) {
    formatPropmpt += ` --niji ${version.value}`
    /* niji5 拥有的风格 */
    style.value && (formatPropmpt += ` --style ${style.value}`)
  }
  /* 尺寸 */
  formatPropmpt += ` --ar ${aspect.value}`
  /* chaos (混乱) */
  formatPropmpt += ` --c ${chaos.value}`
  /* quality */
  formatPropmpt += ` --q ${quality.value}`
  return formatPropmpt
}

/* 上传图片 */
async function uploadImg() {
  const form = new FormData()
  curFile && form.append('file', curFile)
  const res = await axios.post(uploadUrl.value, form, { headers: { 'Content-Type': 'multipart/form-data' } })
  return res?.data?.data
}

/* 修改提示词 */
function handleSelectPrompt(item: any){
	const { prompt: text, aspect: size, isCarryParams  } = item
	prompt.value = text
	size && (aspect.value = size)
	carryOptions.value = isCarryParams ? 1 : 0
}


function checkHasChinese(){
	const isHasPromptChinese = hasChinese(prompt.value)
	const isHasNoPromptChinese = hasChinese(noPrompt.value)
	if(isHasPromptChinese || isHasNoPromptChinese){
		const d = dialog.warning({
			title: '温馨提示',
			content: '您的提示词中包含中文、绘画AI可能无法识别您的中文、我们建议您翻译后进行绘画得到更准确的结果、请问需要翻译后提交么？',
			positiveText: '翻译提示词',
			negativeText: '不需要',
			onPositiveClick: async () => {
				d.loading = true
				const task = []
				isHasPromptChinese && task.push(handleFanyiPrompt())
				isHasNoPromptChinese && task.push(handleFanyiNoPrompt())
				await Promise.all(task)
				handleSubmit()
			},
			onNegativeClick: () => {
				handleSubmit()
			}
		})
	}else{
		handleSubmit()
	}
}

/* 提交绘制任务|图生图 */
async function handleSubmit() {
  let imgUrl = ''
  if (dataBase64.value || curFile)
    imgUrl = await uploadImg()

  const extraParam = formatParams()

  /* 如果使用我们的参数 则去掉用户自己的 */
  if (carryOptions.value)
    prompt.value = removeParameters(prompt.value)

  await fetchDrawTaskAPI({ prompt: prompt.value, imgUrl, extraParam })
  curFile && (curFile = null)
  dataBase64.value = ''
  ms.success('提交绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 提交放大绘制任务 */
async function handleUpscale(item: any, orderId: number) {
  const { id } = item
  await fetchDrawTaskAPI({ drawId: id, action: 2, orderId })
  ms.success('提交放大绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 提交重新生成任务 */
async function handleReGenerate(item: any, orderId: number) {
  const { id } = item
  await fetchDrawTaskAPI({ drawId: id, action: 5, orderId })
  ms.success('提交重新生成绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 提交变体任务 */
async function handleVariation(item: any, orderId: number) {
  const { id } = item
  await fetchDrawTaskAPI({ drawId: id, action: 3, orderId })
  ms.success('提交图片变换绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 提交对单张图片调整任务 */
async function handleVary(item: any, orderId: number) {
  const { id } = item
  await fetchDrawTaskAPI({ drawId: id, action: 7, orderId })
  ms.success('提交图片调整绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 提交对单张图片缩放任务 */
async function handleZoom(item: any, orderId: number) {
  const { id } = item
  await fetchDrawTaskAPI({ drawId: id, action: 6, orderId })
  ms.success('提交图片调整绘制任务成功、请等待绘制结束！')
	if(authStore.token){
		await refreshUserInfo()
	}
  !isLoopIn && queryDrawResult()
}

/* 轮询查询结果 */
async function queryDrawResult() {
  isLoopIn = true
  const res: ResData = await fetchMidjourneyDrawList({ page: 1, size: size.value })
  const { rows, countQueue: queueCount, count } = res.data
  drawList.value = rows || []
  totalCount.value = count
  countQueue.value = queueCount || 0
  const curDrawTask = drawList.value.filter((item: any) => [1, 2].includes(item.status))
  if (curDrawTask.length)
    timer = setTimeout(() => queryDrawResult(), 3000)

  else
    isLoopIn = false
}

onBeforeUnmount(() => {
  clearTimeout(timer)
})

/* 当前绘制中的任务 */
const curDrawTask = computed(() => {
  return drawList.value.filter((item: any) => [1, 2].includes(item.status))
})

/* 下载图片 */
async function handleDownloadImg(item: any) {
  const d = dialog.info({
    title: '下载图片',
    content: '是否确认下载当前图片',
    positiveText: '下载',
    negativeText: '取消',
    onPositiveClick: async () => {
			d.loading = true
			return new Promise(async (resolve) => {
				const { fileInfo } = item
				const { filename, cosUrl } = fileInfo
				const response = await axios.post(downloadUrl, { url: cosUrl }, { responseType: 'blob' })
				const blob = new Blob([response.data], { type: response.headers['content-type'] })
				const urlObject = window.URL.createObjectURL(blob)
				const link = document.createElement('a')
				link.href = urlObject
				link.download = filename
				link.click()
				resolve(true)
			})
    }
  })
}

/* 删除图片 */
async function handleDeleteDraw(item: any) {
  dialog.warning({
    title: '删除记录',
    content: '是否确认删除当前绘制记录？',
    positiveText: '删除',
    negativeText: '取消',
    onPositiveClick: async () => {
      const { id } = item
      const res: ResData = await fetchDownloadImg({ id })
      if (!res.success)
        return ms.error(res.message)
      ms.success('删除绘制记录成功！')
      queryAllDrawList()
    },
  })
}

/* 前往市场 */
function readMore() {
  router.push('/market')
}

/* 复制 */
function copyPrompt(item: any) {
  const { fullPrompt } = item
  copyText({ text: fullPrompt })
  ms.success('复制prompt完成！')
}

function setModel(name: string) {
  model.value = name
  if (name === 'MJ')
    version.value = '5.1'

  if (name === 'NIJI')
    version.value = '5'
}

const calcTips = computed(() => (item: any) => {
  const { progress, status } = item
  if (status === 1)
    return '正在排队中...'
  if (status === 2 && !progress)
    return '正在绘制中...'
  if (status === 2 && progress === 100)
    return '正在存储图片中...'
})

const calcStatusMsg = computed(() => (status: number) => {
  if (status === 1)
    return '等待中'
  if (status === 2)
    return '绘制中'
  if (status === 3)
    return '成功'
  if (status === 4)
    return '失败'
  if (status === 5)
    return '超时'
})

const calcStatusType = computed(() => (status: number) => {
  if (status === 1)
    return ''
  if (status === 2)
    return 'info'
  if (status === 3)
    return 'primary'
  if (status === 4)
    return 'error'
  if (status === 5)
    return 'error'
})

async function refreshUserInfo() {
  refreshLoading.value = true
  try {
    await authStore.getUserInfo()
    refreshLoading.value = false
  }
  catch (error) {
    refreshLoading.value = false
  }
}

const  railStyle = ({
        focused,
        checked
      }: {
        focused: boolean
        checked: boolean
      }) => {
        const style: CSSProperties = {}
        if (checked) {
          style.background = '#2080f0'
          if (focused) {
            style.boxShadow = '0 0 0 2px #2080f040'
          }
        } else {
					style.background = '#00000024'
          if (focused) {
            style.boxShadow = '0 0 0 2px #00000024'
          }
        }
        return style
      }

onMounted(() => {
  queryDrawResult()
	hanleQueryPrompts()
  const container: any = document.getElementById('footer')
  const observer = new IntersectionObserver((entries, observer) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        if (isMore.value) {
          size.value = size.value + 12
          queryAllDrawList()
        }
      }
    })
  })
  observer.observe(container)
})
</script>

<template>
  <div class="grow flex h-screen flex-col lg:pt-0">
    <div class="flex grow flex-col sm:flex-row h-full">
      <div class="p-4 sm:pt-6 bg-[#f8f8f8]  p-4 dark:bg-[#18181c] overflow-y-auto w-full sm:w-[20rem]  shrink-0 border-r-2 border-[#ffffff17]">
        <div class="mt-4 text-sm flex items-center">
          <div class="text-sm mr-1">
            图片尺寸
          </div>

          <div data-tool-target="tooltip-default">
            <NTooltip placement="right-end" trigger="hover">
              <template #trigger>
                <SvgIcon icon="ri:error-warning-line" class="text-base" />
              </template>
              参数释义：生成图片尺寸比例
            </NTooltip>
          </div>
        </div>
        <div class="flex mt-2 py-1 pb-2 space-x-1 overflow-x-auto justify-between scrollbar-none ">
          <button
            v-for="(item, index) in sizeList"
            :key="index"
